<script>
import { email } from "vuelidate/lib/validators";
import routerItems from "../../../constants/routerItems";
import { http } from "../../../services/HttpService";
export default {
  name: "landing-page",
  data() {
    return {
      optin: {
        email: null,
        message: null,
      },
      submitted: false,
      routeItems: routerItems,
    };
  },
  validations: {
    optin: {
      email: { email },
    },
  },
  methods: {
    async optinSubmit() {
      this.submitted = true;
      // stop here if form is invalid
      this.$v.$touch();
      if (this.$v.$invalid) {
        return;
      }
      /* Subscription feeds */
      http()
        .post("/opt-in", { email: this.optin.email })
        .then(
          function (response) {
            if (response) {
              this.optin.message = response.data.message;
              this.optin.email = null;
            }
          }.bind(this)
        )
        .catch(
          function (error) {
            this.optin.message = error.response.data.message;
          }.bind(this)
        );
    },
  },
};
</script>
